package CSW13;

public class BitwiseOperator {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a=0;
		int b=1;
		
		System.out.println("a= "+a+" b= "+b);
		System.out.println("a& :"+(a&b));
		System.out.println("a^b :"+(a^b));
		System.out.println("a|b : "+(a|b));
		System.out.println("~b :"+(~b));

	}

}
